init_pop = ((b-d)/(4*q)*ones(1,4));

% Find basic reproductive ratios
R0_I_DF = (tau.*E.^2)/(a*d+sigma);
R0_D_IF = (beta.*E.^2)/(d+alpha+gamma);

SP = (d + alpha + gamma).*(alpha.^2 + (-beta.*E.^2 + d + gamma).*alpha + E.^2.*beta.*(b - d))./(beta.^2.*E.^4.*q);
