SP = zeros(length(E),1);
SG = zeros(length(E),1);
IP = zeros(length(E),1);
IG = zeros(length(E),1);
N = zeros(length(E),1);
for i=1:length(E)
    [~,x] = sociality_slowinfo_mex(t_max,a,b,E(i),d,q,alpha,beta,gamma,sigma,tau,eqtol,init_pop);
    
    SP(i) = x(end,1);
    SG(i) = x(end,2);
    IP(i) = x(end,3);
    IG(i) = x(end,4);
    N(i) = SP(i)+SG(i)+IP(i)+IG(i);
end

